package Rev;

public class Break_continue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//break
		for(int i=1;i<100;i++) {
			
			//System.out.println("loop  "+i);
			if(i==50) 
			break;
			
			
		}
		
	//continue	
for(int i=1;i<10;i++) {
	
	    if(i==5 || i==6) 
		  continue;
	 
			
			System.out.println("loop  "+i);
			
			
			
		}

	}

}
